<script lang="ts">
    import { onMount } from 'svelte';
    import ZooBanner from '$lib/Zoo.svelte';
    import IdCard from '$lib/assets/IdCard.svg';
    import { base } from '$app/paths';


    const skyColor = [
        '#1A1A2E', // 02h
        '#1B263B', // 04h
        '#FFD6D1', // 06h
        '#B3E5FC', // 08h
        '#81D4FA', // 10h
        '#81D4FA', // 12h
        '#81D4FA', // 14h
        '#81D4FA', // 16h
        '#81D4FA', // 18h
        '#334b63', // 20h
        '#1B263B', // 22h
        '#1A1A2E'  // 00h 
    ];

    const chameleonPalettes = {
        green: {
            color1: '#6E8823',
            color2: '#90AD28',
            color3: '#8EA628',
            color4: '#E3E63B',
            color5: '#B8CC2B',
            color6: '#76821B'
        },
        red: {
            color1: '#882325',
            color2: '#AD282A',
            color3: '#A6282A',
            color4: '#E63B3E',
            color5: '#CC2B2E',
            color6: '#821B1D'
        },
        purple: {
            color1: '#88237E',
            color2: '#AD28A0',
            color3: '#A62899',
            color4: '#E63BD5',
            color5: '#CC2BBC',
            color6: '#821B78'
        },
        blue: {
            color1: '#235988',
            color2: '#286FAD',
            color3: '#286BA6',
            color4: '#3B96E6',
            color5: '#2B81CC',
            color6: '#1B5282'
        },
        yellow: {
            color1: '#6E8823',
            color2: '#90AD28',
            color3: '#8EA628',
            color4: '#E3E63B',
            color5: '#B8CC2B',
            color6: '#76821B'
        }
    };


    const hour = new Date().getHours();
    $effect(() => {
        document.body.style.backgroundColor = skyColor[Math.floor(hour / 2)];
        document.getElementById('skyTree')?.setAttribute('fill', skyColor[Math.floor(hour / 2)]);
    });


    type ChameleonColor = "green" | "red" | "purple" | "blue" | "yellow";
    let chameleonColor = $state<ChameleonColor>('green');

	let chameleonActiveColor = $derived(
        chameleonColor ? chameleonPalettes[chameleonColor] : chameleonPalettes.green
    );

    
    
    let facts = $state([
        {
            "title": "",
            "icon": "",
            "description": "",
            "chameleonColor": "",
            "background": ""
        }
    ]);
    let currentFacts = $state(0);
    let totalFacts = $state(0);
    let loaded = $state(false);
    let unlockInteraction = $derived(
        loaded && currentFacts >= totalFacts - 1
    );
    let unlockLevel = $derived(
        loaded ? Math.round((currentFacts + 1) / totalFacts * 100) : 0
    );


    function syncChameleonColorIndex() {
        chameleonColor = (facts[currentFacts]?.chameleonColor as ChameleonColor) || 'green';
    }
    function nextSlide() {
        if (currentFacts < totalFacts - 1) currentFacts += 1;
        setTimeout(syncChameleonColorIndex, 500);
    }
    function prevSlide() {
        if (currentFacts > 0) currentFacts -= 1;
        setTimeout(syncChameleonColorIndex, 500);
    }

    function interactionPage() {
        if (unlockInteraction) {
            window.location.href = 'chameleon';
        }
    }
    

    // let scrollProgress = $state(0);
    let eyeOffsetX = $state(0);
    let eyeOffsetY = $state(0);

    // coordonnées de l'œil
    let eyeCenterX = 0;
    let eyeCenterY = 0;

    const minX = -3;
    const maxX = 17;
    const minY = -3;
    const maxY = 5;

    onMount(function() {
        // Facts ---------------------------------------
        fetch(`${ base }/facts.json`)
        .then(response => response.json())
        .then(data => {
            facts = data;
            loaded = true;
            totalFacts = facts.length;
        });
        

        // Eye -----------------------------------------
        const eye = document.getElementById('eye-chameleon1');
        if (eye) {
            const rect = eye.getBoundingClientRect();
            eyeCenterX = rect.left + rect.width / 2;
            eyeCenterY = rect.top + rect.height / 2;
        }

        const handleMouseMove = (event: MouseEvent) => {
            const dx = event.clientX - eyeCenterX;
            const dy = event.clientY - eyeCenterY;

            eyeOffsetX += (Math.max(minX, Math.min(maxX, dx)) - eyeOffsetX) * 0.2;
            eyeOffsetY += (Math.max(minY, Math.min(maxY, dy)) - eyeOffsetY) * 0.2;
        };


        // Event listener ------------------------------
        window.addEventListener('mousemove', handleMouseMove);
        return () => {
            window.removeEventListener('mousemove', handleMouseMove);
        };
    });
</script>

