<script lang="ts">
    import ZooBanner from '$lib/Zoo.svelte';
    import IdCard from '$lib/assets/IdCard.svg';

    import { onMount } from 'svelte';


    let chameleonColorIndex = 0;
    let chameleonColor1 = ['#6E8823', '#882325', '#88237E', '#235988'];
    let chameleonColor2 = ['#90AD28', '#AD282A', '#AD28A0', '#286FAD'];
    let chameleonColor3 = ['#8EA628', '#A6282A', '#A62899', '#286BA6'];
    let chameleonColor4 = ['#E3E63B', '#E63B3E', '#E63BD5', '#3B96E6'];
    let chameleonColor5 = ['#B8CC2B', '#CC2B2E', '#CC2BBC', '#2B81CC'];
    let chameleonColor6 = ['#76821B', '#821B1D', '#821B78', '#1B5282'];


    let scrollProgress = 0;
    let eyeOffsetX = 0;
    let eyeOffsetY = 0;
    
    onMount(() => {
        let eyeCenterX = 0;
        let eyeCenterY = 0;

        const eye = document.getElementById('eye-chameleon1');
        if (eye) {
            const rect = eye.getBoundingClientRect();
            eyeCenterX = rect.left + rect.width / 2;
            eyeCenterY = rect.top + rect.height / 2;
        }

        const minX = -3;
        const maxX = 17;
        const minY = -3;
        const maxY = 5;

        function handleScroll() {
            const scrollTop = window.scrollY;
            const docHeight = document.documentElement.scrollHeight - window.innerHeight;
            scrollProgress = docHeight > 0 ? (scrollTop / docHeight) * 100 : 0;
        }

        function handleMouseMove(event: MouseEvent) {
            const mouseX = event.clientX;
            const mouseY = event.clientY;

            let dx = mouseX - eyeCenterX;
            let dy = mouseY - eyeCenterY;

            eyeOffsetX += (Math.max(minX, Math.min(maxX, dx)) - eyeOffsetX) * 0.2;
            eyeOffsetY += (Math.max(minY, Math.min(maxY, dy)) - eyeOffsetY) * 0.2;
        }

        window.addEventListener('scroll', handleScroll);
        window.addEventListener('mousemove', handleMouseMove);
        handleScroll();

        return () => {
            window.removeEventListener('scroll', handleScroll);
            window.removeEventListener('mousemove', handleMouseMove);
        };
    });

    

    let currentSlide = 0;
    const slideCount = 4;

    function syncChameleonColorIndex() {
        chameleonColorIndex = currentSlide;
    }
    function nextSlide() {
        if (currentSlide < slideCount - 1) currentSlide += 1;
        setTimeout(syncChameleonColorIndex, 500);
    }
    function prevSlide() {
        if (currentSlide > 0) currentSlide -= 1;
        setTimeout(syncChameleonColorIndex, 500);
    }
</script>

